mapper.select:

	select
    	count(*)
	from
    	zero_member
	where
    	memberName = #{memberName}
	and
    	memberTel = #{memberTel}
	and
    	memberId = #{memberId}

mapper.update:
	UPDATE 
    	zero_member 
	SET 
    	memberPw = #{memberPw}
	where
    	memberName = #{memberName}
	and
    	memberTel = #{memberTel}
	and
    	memberId = #{memberId}

update zero_member
set naverLogin = #{naverLogin}
where memberMail = #{memberMail}

update zero_member
set memberPw = #{memberPw}, memberName=#{memberName}, 
memberMail=#{memberMail}, memberAddr1 = #{memberAddr1}, 
memberAddr2 = #{memberAddr2}, memberAddr3 = #{memberAddr3},
memberTel = #{memberTel}
where memberId = #{memberId}
